SELECT distinct s.day_age , s.uid_num id, s.farm_id , s.house_id , s.record_datetime, ROUND(s.set_temp,1) set_temp , ROUND(s.high_alarm_temp,1) high_alarm_temp , ROUND(s.low_alarm_temp,1) low_alarm_temp , s.set_humidity , s.memo , s.create_person , s.create_date , s.create_time , s.modify_person , s.modify_date , s.modify_time , s.high_alarm_humidity , s.low_alarm_humidity , s.super_high_alarm_humidity , s.super_low_alarm_humidity , s.super_high_alarm_temp , s.super_low_alarm_temp , ROUND(s.set_negative_pressure,1) set_negative_pressure , ROUND(s.high_alarm_negative_pressure,1) high_alarm_negative_pressure , ROUND(s.low_alarm_negative_pressure,1) low_alarm_negative_pressure , s.super_high_alarm_negative_pressure , s.super_low_alarm_negative_pressure , ROUND(s.set_lux,1) set_lux , ROUND(s.high_lux,1) high_lux , ROUND(s.low_lux,1) low_lux , ROUND(s.set_co2,1) set_co2 , ROUND(s.high_alarm_co2,1) high_alarm_co2 , ROUND(s.low_alarm_co2,1) low_alarm_co2 , s.super_high_alarm_co2 , s.super_low_alarm_co2 , ROUND(s.set_water_deprivation,1) set_water_deprivation , ROUND(s.high_water_deprivation,1) high_water_deprivation , ROUND(s.low_water_deprivation,1) low_water_deprivation , s.super_high_water_deprivation , s.super_low_water_deprivation , s.alarm_type, d.start_time, d.end_time, ROUND(s.high_temp_warning,1) high_temp_warning, ROUND(s.low_temp_warning,1) low_temp_warning, ROUND(s.high_lux_warning,1) high_lux_warning, ROUND(s.low_lux_warning,1) low_lux_warning, ROUND(s.high_co2_warning,1) high_co2_warning FROM s_b_dayage_temp_sub s left join s_b_dayage_setting_sub d on d.uid_num = s.uid_num WHERE 1=1
and s.farm_id = #{farmId}
and s.house_id = #{houseId}
and s.day_age = #{day_age}
and s.uid_num = #{uid_num}
and HOUR(s.record_datetime) =0 and s.alarm_type = #{alarm_type} ORDER BY s.farm_id,s.house_id,s.day_age

