UPDATE s_d_device_sub u SET u.is_alarm = #{is_alarm}  , u.modify_person = #{modify_person}
  , u.modify_date = #{modify_date}
  WHERE exists(select 1 FROM s_b_devi_house h
left join
s_d_device d
on d.device_code = h.device_code
left join
(select show_column,main_id,sensor_no from s_d_device_sub ) s
on s.main_id = d.main_id
left join
s_d_code c
on c.biz_code = s.show_column
where 
c.code_type = 'SENSOR_LOCATION' 
and c.biz_code !=#{biz_code}
and h.house_id = #{houseId}

	and d.device_code = #{device_code}

and s.main_id = u.main_id)
and u.show_column = #{biz_code}

mapper.select:
SELECT c.code_type, c.biz_code, c.code_name, c.bak1, c.bak2, c.code_desc, s.is_alarm FROM s_b_devi_house h left join s_d_device d on d.device_code = h.device_code left join s_d_device_sub s on s.main_id = d.main_id left join s_d_code c on c.biz_code = s.show_column where c.code_type = #{code_type} and c.biz_code !='outside_temp' and h.house_id = #{houseId}
and d.device_code = #{device_code}
order by c.bak1

SELECT s.uid_num , max(s.farm_id) farm_id, max(s.house_id) house_id, max(s.day_age) day_age, max(s.alarm_type) alarm_type, IFNULL(ROUND(s.set_values,1),"-") set_values , IFNULL(ROUND(s.high_alarm_values,1),"-") high_alarm_values , IFNULL(ROUND(s.low_alarm_values,1),"-") low_alarm_values , IFNULL(ROUND(s.high_warning_values,1),"-") high_warning_values , IFNULL(ROUND(s.low_warning_values,1),"-") low_warning_values , s.super_high_alarm_values , s.super_low_alarm_values , s.memo , s.alarm_operation , s.approve_status , u.user_real_name as modify_person , s.modify_date , max(s.modify_time) modify_time, DATE_FORMAT(s.start_time,'%H:%i:%s') start_time , DATE_FORMAT(s.end_time,'%H:%i:%s') end_time FROM s_b_dayage_setting_sub_his s left join s_d_user u on u.id = s.modify_person WHERE 1=1
and s.farm_id = #{farmId}
and s.house_id = #{houseId}
and s.alarm_type = #{alarm_type} and DATEDIFF(DATE_FORMAT(s.modify_time,'%Y-%m-%d'),str_to_date(#{start_date},'%Y-%m-%d'))>=0 and DATEDIFF(str_to_date(#{end_date},'%Y-%m-%d'),DATE_FORMAT(s.modify_time,'%Y-%m-%d'))>=0 group by s.farm_id,s.house_id,s.alarm_type,s.day_age ORDER BY s.modify_time desc,s.farm_id,s.house_id,s.day_age
