mapper.select:
select d.id, d.farm_code, d.farm_name_chs, d.farm_name_en, d.feed_method, d.layers, d.rows, d.feed_density, d.h_length, d.h_width, d.h_height, d.feed_purpose, d.corporation, (select a.name from s_d_area_china a where a.id = d.farm_add1) as farm_add1, (select a.name from s_d_area_china a where a.id = d.farm_add2) as farm_add2, (select a.name from s_d_area_china a where a.id = d.farm_add3) as farm_add3, d.farm_add4, d.farm_add5, d.memo_1, d.memo_2, d.freeze_status, d.create_person, d.create_date, d.create_time, d.modify_person, d.modify_date, d.modify_time from (select * from s_d_farm a where exists(select id from s_d_organization b where a.id=b.id)) d where d.freeze_status=0
and d.farm_code = #{farmCode}
and d.farm_name_chs = #{farmNameChs}
and d.id = #{farmId}
and exists(select usr.role_level, org.id, org.parent_id, org2.max_level from s_d_organization org left join v_user usr on usr.id= #{user_id} and usr.obj_type=2 and usr.obj_id = org.id join (select max(level_id) max_level from s_d_organization) org2 on 1=1 where usr.id is not null and org.level_id=(org2.max_level-1) and (d.id = org.id or usr.role_level=1))

select id , house_code , house_name , house_type , farm_id , feed_density , house_length , house_width , house_height , memo_1 , memo_2 , freeze_status , create_person , create_date , create_time , modify_person , modify_date , modify_time from s_d_house where freeze_status="0"
and farm_id = #{farmId}
order by farm_id,house_code

select id , house_code , house_name , house_type , farm_id , feed_density , house_length , house_width , house_height , memo_1 , memo_2 , freeze_status , create_person , create_date , create_time , modify_person , modify_date , modify_time, code.code_name house_type_name from s_d_house LEFT JOIN (SELECT * FROM s_d_code WHERE code_type='HOUSE_TYPE') code ON code.biz_code = house_type where freeze_status = 0
and id = #{houseId}
and house_name = #{house_name}
and farm_id = #{farmId}

		SELECT distinct batch_no
		FROM   s_b_batch_curr
	    where status =1

         and farm_id = #{farmId} 


         and house_code = #{houseId} 


         and batch_no = #{batch_no} 

        order by batch_id desc

SELECT s_d_farm.*, a1.name as province, a2.name as city, a3.name as area, c1.code_name, c2.code_name as code_name1, org.parent_id FROM s_d_farm left join s_d_area_china a1 on a1.id=s_d_farm.farm_add1 left join s_d_area_china a2 on a2.id=s_d_farm.farm_add2 left join s_d_area_china a3 on a3.id=s_d_farm.farm_add3 left join s_d_code as c1 on c1.biz_code=s_d_farm.feed_method and c1.code_type='FEED_METHOD' left join s_d_code as c2 on c2.biz_code=s_d_farm.feed_type and c2.code_type='FEED_TYPE' left join s_d_organization org on org.id = s_d_farm.id where s_d_farm.freeze_status='0'
and s_d_farm.id = #{id}
and (exists(select usr.role_level, org.id, org.parent_id, org2.max_level from s_d_organization org left join v_user usr on usr.id= #{user_id} and usr.obj_type=2 and usr.obj_id = org.id join (select max(level_id) max_level from s_d_organization) org2 on 1=1 where usr.id is not null and org.level_id=(org2.max_level-1) and s_d_farm.id = org.id) or s_d_farm.create_person = #{user_id} )

SELECT s_d_house.* , c1.code_name, s_d_farm.farm_name_chs FROM s_d_house left join s_d_farm on s_d_farm.id=s_d_house.farm_id left join s_d_code as c1 on c1.biz_code=s_d_house.house_type and c1.code_type='HOUSE_TYPE' where s_d_house.freeze_status='0'
and (exists(select usr.role_level, org.id, org.parent_id, org2.max_level from s_d_organization org left join v_user usr on usr.id= #{user_id} and usr.obj_type=2 and usr.obj_id = org.id join (select max(level_id) max_level from s_d_organization) org2 on 1=1 where usr.id is not null and org.level_id=org2.max_level and (case when usr.role_level=org2.max_level then org.organization_id else (select distinct organization_id from s_d_organization where id = org.parent_id and level_id = (org2.max_level-1)) end) = (case when usr.role_level=org2.max_level then s_d_house.id else s_d_house.farm_id end)) or s_d_house.create_person =#{user_id})
and s_d_house.id = #{id}
and s_d_house.farm_id = #{farmId}

SELECT s_d_batch.*, s_d_farm.farm_name_chs, s_d_code.code_name FROM s_d_batch left join s_d_farm on s_d_farm.id=s_d_batch.farm_id left join s_d_code on s_d_code.biz_code=s_d_batch.operation_type and s_d_code.code_type='OPERATION_TYPE' where 1=1
and s_d_batch.id = #{pd.id}
order by s_d_batch.id desc

		SELECT *
		FROM   s_d_code
		where 1=1

         and code_type = #{code_type} 


		 and biz_code = #{biz_code}

		SELECT * FROM s_d_area_china
		where status=1

         	and parent_id = #{parent_id} 


         	and level = #{level} 

        order by sort

mapper.update:
update  s_d_farm
	set modify_person	= #{modify_person}


				,modify_date 	= #{modify_date}


				,modify_date 	= current_date




				,modify_time 	= #{modify_time}


				,modify_time 	= current_time



		,farm_code				= #{farm_code}


		,farm_name_chs				= CONCAT(#{farm_name_chs}, '(', id, ')')


		,farm_name_chs				= #{farm_name}


		,farm_name_en			 = #{farm_name_en}


		,feed_method			 = #{feed_method}


		,feed_type			 = #{feed_type}


		,freeze_status			 = #{freeze_status}


		,farm_add1			 = #{farm_add1}


		,farm_add2			 = #{farm_add2}


		,farm_add3			 = #{farm_add3}

	where 
		id = #{id}

update  s_d_batch
	set 
	day_age_curr = #{day_age_curr}

	,count	= #{count}


	,count	= null

	where id = #{id}

mapper.select:
		select * from s_d_device t
		where exists (
		   select 1 from (select device_code from s_b_devi_house a
		   INNER JOIN s_d_organization c on c.organization_id = a.house_id
		   INNER JOIN s_d_house b on c.organization_id = b.house_code and b.freeze_status = '0'

         		and a.house_id = #{house_id}

		   ) t1
		where t.device_code = t1.device_code)

         	and t.device_code = #{device_code}

select * from s_b_devi_house a
where 1=1

	and a.house_id <> #{house_id}


	and a.device_code = #{device_code}

SELECT c.biz_code location_code ,c.code_name location_name ,IFNULL(dds.sensor_no,'') sensor_no ,IFNULL(dds.main_id,'') main_id ,IFNULL(dds.sensor_code,'') sensor_type FROM s_d_code c LEFT JOIN s_d_device_sub dds ON c.biz_code = dds.show_column
AND dds.sensor_code = #{sensor_code}
AND dds.main_id = #{main_id}
WHERE c.code_type='SENSOR_LOCATION' -- ORDER BY c.bak1, dds.main_id, dds.sensor_code ORDER BY sensor_no

mapper.delete:
DELETE FROM s_b_devi_house
	WHERE 1=1
		AND house_id = #{house_id}

			AND device_code = #{device_code}

DELETE FROM s_d_device_sub
	WHERE main_id = #{main_id}

				AND sensor_code = #{sensor_code}


				AND show_column = #{show_column}

mapper.update:
update  s_d_house
	set modify_person	= #{modify_person},
		modify_date 	= #{modify_date},
		modify_time 	= #{modify_time}

		,house_code				= #{house_code}


		,house_name				= #{house_name}


		,house_type			 = #{house_type}


		,farm_id			 = #{farm_id}


		,freeze_status			 = #{freeze_status}

	where 
		id = #{id}

mapper.delete:
		delete from s_b_devi_house
		where 1=1

         	and house_id = #{house_id} 


         	and farm_id = #{farm_id}
